import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import BackButton from '../components/BackButton';
import axios from 'axios';
import './Post.css';

function Post() {
  const navigate = useNavigate();
  const [username, setUsername] = useState('');
  const [usernames, setUsernames] = useState([]);
  const [message, setMessage] = useState('');
  const [isVerified, setIsVerified] = useState(false);

  const handleVerify = () => {
    if (username && !usernames.includes(username)) {
      setUsernames([...usernames, username]);
      setUsername('');
      setIsVerified(false);
    }
  };

  const handleRemoveUsername = (usernameToRemove) => {
    setUsernames(usernames.filter(u => u !== usernameToRemove));
  };

  const handlePost = async () => {
    if (usernames.length > 0 && message) {
      try {
        await axios.post('http://localhost:5000/api/rumors', {
          message,
          usernames
        });
        navigate('/');
      } catch (error) {
        console.error('Error posting rumor:', error);
        alert('Failed to post rumor. Please try again.');
      }
    }
  };

  return (
    <div className="post-container">
      <BackButton />
      <div className="username-input-section">
        <input
          type="text"
          value={username}
          onChange={(e) => {
            setUsername(e.target.value);
            setIsVerified(false);
          }}
          placeholder="Enter username"
          className="username-input"
        />
        <button
          onClick={() => setIsVerified(true)}
          className="verify-button"
        >
          Verify
        </button>
        {isVerified && (
          <button
            onClick={handleVerify}
            className="add-button"
          >
            +
          </button>
        )}
      </div>

      {usernames.length > 0 && (
        <div className="usernames-list">
          {usernames.map((name, index) => (
            <div
              key={index}
              className="username-tag"
              onClick={() => handleRemoveUsername(name)}
            >
              {name}
            </div>
          ))}
        </div>
      )}

      <textarea
        value={message}
        onChange={(e) => setMessage(e.target.value)}
        placeholder="Enter your rumor message"
        className="message-input"
      />

      <button
        onClick={handlePost}
        disabled={usernames.length === 0 || !message}
        className="post-button"
      >
        Post
      </button>
    </div>
  );
}

export default Post;
